import React, { useState } from 'react';
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 40px 20px;
  background-color: #f4f4f4;
  min-height: 100vh;
  box-sizing: border-box;
`;

const ContentWrapper = styled.div`
  width: 100%;
  max-width: 1200px;
  padding: 20px;
  background-color: #fff;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  border-radius: 8px;
`;

const Section = styled.section`
  margin-bottom: 40px;
`;

const SectionTitle = styled.h2`
  margin-bottom: 20px;
  color: #333;
  font-size: 24px;
`;

const TextInput = styled.textarea`
  padding: 10px;
  border-radius: 5px;
  border: 1px solid #ccc;
  font-size: 16px;
  width: 100%;
  max-width: 100%;
  min-height: 100px;
`;

const TermsList = styled.div`
  max-height: 300px;
  overflow-y: auto;
  background-color: #f9f9f9;
  border: 1px solid #ccc;
  border-radius: 5px;
  padding: 15px;
`;

const TermItem = styled.div`
  padding: 10px;
  border-bottom: 1px solid #e9e9e9;
`;

const TermText = styled.span`
  color: #333;
`;

const Button = styled.button`
  margin-top: 20px;
  padding: 15px 25px;
  background-color: #007bff;
  color: white;
  border: none;
  border-radius: 5px;
  font-size: 18px;
  cursor: pointer;
  transition: background-color 0.3s;

  &:hover {
    background-color: #0056b3;
  }
`;

const ExportButton = styled(Button)`
  background-color: #28a745;

  &:hover {
    background-color: #218838;
  }
`;

const ScrTextChunker = () => {
  const [text, setText] = useState('');
  const [terms, setTerms] = useState([]);
  const [loading, setLoading] = useState(false);
  const [studylevel, setStudyLevel] = useState('');

  const handleGenerateTerms = async () => {
    setLoading(true);
    

    console.log("Input text:", text);

    try {
      const response = await fetch('http://localhost:5001/extract_terms', {  // Directly call Flask backend
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ text, level: studyLevel })
      });

      console.log("Request body:", { text, level: studyLevel });

      const data = await response.json();

      console.log("Response data:", data);

      // Parse the returned string into individual terms and definitions
      const parsedTerms = data.terms_and_definitions
        .split(/\n\d+\.\s/)  // Split based on the numbered list pattern
        .filter(entry => entry.trim() !== '')  // Remove empty entries
        .map(entry => {
          const match = entry.match(/^\*\*(.*?)\*\*:\s(.*)$/);
          if (match) {
            return { termName: match[1].trim(), definition: match[2].trim() };
          } else {
            return null;
          }
        })
        .filter(item => item !== null);

      if (parsedTerms.length === 0) {
        console.warn("No terms were parsed.");
        alert("No terms could be extracted. Please check the input and try again.");
      }

      setTerms(parsedTerms);
    } catch (error) {
      console.error("Error generating terms:", error);
    }
    setLoading(false);
  };

  return (
    <Container>
      <ContentWrapper>
        <Section>
          <SectionTitle>Input Text</SectionTitle>
          <TextInput
            value={text}
            onChange={(e) => setText(e.target.value)}
            placeholder="Enter or paste your text here..."
          />
          <TextInput
            value={text}
            onChange={(e) => setStudyLevel(e.target.value)}
            placeholder="Enter your level of study (e.g., High School, College)"
          />
          <Button onClick={handleGenerateTerms} disabled={loading || !text}>
            {loading ? "Generating..." : "Generate Terms"}
          </Button>
        </Section>

        <Section>
          <SectionTitle>Generated Terms & Definitions</SectionTitle>
          <TermsList>
            {terms.map((term, index) => (
              <TermItem key={index}>
                <TermText><strong>{term.termName}:</strong> {term.definition}</TermText>
              </TermItem>
            ))}
          </TermsList>
        </Section>

        <Button>Generate Images</Button>
        <ExportButton>Export File</ExportButton>
      </ContentWrapper>
    </Container>
  );
};

export default ScrTextChunker;
